contracts/SequencerAgent.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../interface/ILockingPool.sol";
import "../interface/ILockingInfo.sol";
import "../interface/IL1ERC20Bridge.sol";



contract SequencerAgent is ContextUpgradeable {
    address public dealer;
    ILockingPool public lockingPool;
    ILockingInfo public lockingInfo;
    IERC20 public metis;

    event SequencerAgentInitialized(address _lockingPool, address _l1Token, address _dealer);
    event SequencerLocked(address _signer, uint256 _amount, bytes _signerPubKey);
    event StakingAmountIncreased(uint256 _amount);
    event RewardsClaimed(uint256 _amount);
    event StakingAmountWithdrawn(address indexed _recipient, uint256 _amount);

    modifier onlyDealer() {
        require(msg.sender == dealer, "SequencerAgent: only dealer");
        _;
    }

    function initialize(address _dealer, ILockingPool _lockingPool, ILockingInfo _lockingInfo, address _metis) external initializer {
        dealer = _dealer;
        lockingPool = _lockingPool;
        lockingInfo = _lockingInfo;
        metis = IERC20(_metis);
    }

    function lock(address _sequencerSigner, address _rewardRecipient, uint256 _amount, bytes memory _signerPubKey) public onlyDealer {
        require(_sequencerSigner != address(0) && _rewardRecipient != address(0), "SequencerAgent: zero address");
        require(_amount > 0, "SequencerAgent: zero amount");

        metis.approve(address(lockingInfo), type(uint256).max);
        lockingPool.lockWithRewardRecipient(_sequencerSigner, _rewardRecipient, _amount, _signerPubKey);
        emit SequencerLocked(_sequencerSigner, _amount, _signerPubKey);
    }

    function increaseStakingAmount(
        uint256 sequencerId,
        uint256 _amount
    ) external onlyDealer{
        require(_amount > 0, "SequencerAgent: invalid amount");
        IERC20(ILockingInfo(lockingInfo).l1Token()).approve(address(lockingInfo), _amount);
        lockingPool.relock(sequencerId, _amount, false);
        emit StakingAmountIncreased(_amount);
    }

    // l2GasLimit * discount < msg.value
    function claimRewards(
        uint256 sequencerId,
        uint32 _l2GasLimit
    ) external payable onlyDealer {
        lockingPool.withdrawRewards{value: msg.value}(sequencerId, _l2GasLimit);
    }

    function withdrawStakingAmount(
        uint256 sequencerId,
        address _recipient,
        uint256 _amount
    ) external onlyDealer() {
        require(_amount > 0, "SequencerAgent: invalid amount");
        require(
            _recipient != address(0),
            "SequencerAgent: invalid recipient"
        );
        lockingPool.withdraw(sequencerId, _amount);
        _bridgeTo(_recipient, _amount, 0);
        emit StakingAmountWithdrawn(_recipient, _amount);
    }


    function unlock(uint256 sequencerId, uint32 l2Gas) public payable onlyDealer {
        lockingPool.unlock{value: msg.value}(sequencerId, l2Gas);
    }

    function unlockClaim(uint256 sequencerId, uint32 l2Gas) public payable onlyDealer {
        lockingPool.unlockClaim{value: msg.value}(sequencerId, l2Gas);
        metis.transfer(dealer, metis.balanceOf(address(this)));
    }

    function _bridgeTo(
        address _recipient,
        uint256 _amount,
        uint32 _l2gas
    ) internal {
        if (_amount == 0) {
            return;
        }
        address bridge = lockingInfo.bridge();
        uint256 l2ChainId = lockingInfo.l2ChainId();
        IERC20(ILockingInfo(lockingInfo).l1Token()).approve(bridge, _amount);
        IL1ERC20Bridge(bridge).depositERC20ToByChainId{value: msg.value}(
            l2ChainId,
            ILockingInfo(lockingInfo).l1Token(), 
            ILockingInfo(lockingInfo).l2Token(), 
            _recipient, 
            _amount, 
            _l2gas, "");
    }

    function withdrawMetisFromAgent( uint256 _amount) external onlyDealer {
        IERC20(metis).transfer(msg.sender, _amount);
    }
}

