// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {ISequencerInfo} from "./ISequencerInfo.sol";
import {ILockingInfo as ILockingInfoRaw} from "./ILockingInfoRaw.sol";

interface ILockingInfo is ILockingInfoRaw {
    function bridge() external view returns(address);
    function l1Token() external view returns(address);
    function l2Token() external view returns(address);
    function minLock() external view returns(uint256);
    function maxLock() external view returns(uint256);
    function l2ChainId() external view returns(uint256);

}

interface/ILockingInfoRaw.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import {ISequencerInfo} from "./ISequencerInfo.sol";

interface ILockingInfo {
    /**
     * @dev Emitted when min lock amount update in 'UpdateMinAmounts'
     * @param _newMinLock new min lock.
     */
    event SetMinLock(uint256 _newMinLock);

    /**
     * @dev Emitted when min lock amount update in 'UpdateMaxAmounts'
     * @param _newMaxLock new max lock.
     */
    event SetMaxLock(uint256 _newMaxLock);

    /**
     * @dev Emitted when the reward payer is changed
     * @param _payer new reward payer
     */
    event SetRewardPayer(address _payer);

    /**
     * @dev Emitted when sequencer locks in '_lockFor()' in LockingPool.
     * @param signer sequencer address.
     * @param sequencerId unique integer to identify a sequencer.
     * @param nonce to synchronize the events in themis.
     * @param activationBatch sequencer's first epoch as proposer.
     * @param amount locking amount.
     * @param total total locking amount.
     * @param signerPubkey public key of the sequencer
     */
    event Locked(
        address indexed signer,
        uint256 indexed sequencerId,
        uint256 nonce,
        uint256 indexed activationBatch,
        uint256 amount,
        uint256 total,
        bytes signerPubkey
    );

    /**
     * @dev Emitted when the sequencer increase lock amount in 'relock()'.
     * @param sequencerId unique integer to identify a sequencer.
     * @param amount locking new amount
     * @param total the total locking amount
     */
    event Relocked(uint256 indexed sequencerId, uint256 amount, uint256 total);

    /**
     * @dev Emitted when the sequencer reduce lock amount in 'withdraw()'.
     * @param sequencerId unique integer to identify a sequencer.
     * @param amount withdraw new amount
     */
    event Withdraw(uint256 indexed sequencerId, uint256 amount);

    /**
     * @dev Emitted when sequencer relocking in 'relock()'.
     * @param sequencerId unique integer to identify a sequencer.
     * @param nonce to synchronize the events in themis.
     * @param newAmount the updated lock amount.
     */
    event LockUpdate(
        uint256 indexed sequencerId,
        uint256 indexed nonce,
        uint256 indexed newAmount
    );

    /**
     * @dev Emitted when sequencer withdraw rewards in 'withdrawRewards' or 'unlockClaim'
     * @param sequencerId unique integer to identify a sequencer.
     * @param recipient the address receive reward tokens
     * @param amount the reward amount.
     * @param totalAmount total rewards has liquidated
     */
    event ClaimRewards(
        uint256 indexed sequencerId,
        address recipient,
        uint256 indexed amount,
        uint256 indexed totalAmount
    );

    /**
     * @dev Emitted when sequencer unlocks in '_unlock()'.
     * @param user address of the sequencer.
     * @param sequencerId unique integer to identify a sequencer.
     * @param nonce to synchronize the events in themis.
     * @param deactivationBatch  last batch for sequencer.
     * @param deactivationTime unlock block timestamp.
     * @param unlockClaimTime when user can claim locked token.
     * @param amount locking amount
     */
    event UnlockInit(
        address indexed user,
        uint256 indexed sequencerId,
        uint256 nonce,
        uint256 deactivationBatch,
        uint256 deactivationTime,
        uint256 unlockClaimTime,
        uint256 indexed amount
    );

    /**
     * @dev Emitted when sequencer unlocks in 'unlockClaim()'
     * @param user address of the sequencer.
     * @param sequencerId unique integer to identify a sequencer.
     * @param amount locking amount.
     * @param total total locking amount.
     */
    event Unlocked(
        address indexed user,
        uint256 indexed sequencerId,
        uint256 amount,
        uint256 total
    );

    /**
     * @dev Emitted when batch update in  'batchSubmitRewards'
     * @param _newBatchId new batchId.
     *
     *      Note from maintainer:
     *      for compatibility, the event is retained
     */
    event BatchSubmitReward(uint256 _newBatchId);

    /**
     * @dev Emitted when the sequencer public key is updated in 'updateSigner()'.
     * @param sequencerId unique integer to identify a sequencer.
     * @param nonce to synchronize the events in themis.
     * @param oldSigner oldSigner old address of the sequencer.
     * @param newSigner newSigner new address of the sequencer.
     * @param signerPubkey signerPubkey public key of the sequencer.
     */
    event SignerChange(
        uint256 indexed sequencerId,
        uint256 nonce,
        address indexed oldSigner,
        address indexed newSigner,
        bytes signerPubkey
    );

    function newSequencer(
        uint256 _id,
        address _owner,
        address _signer,
        uint256 _amount,
        uint256 _batchId,
        bytes calldata _signerPubkey
    ) external;

    function increaseLocked(
        uint256 _seqId,
        uint256 _nonce,
        address _owner,
        uint256 _locked,
        uint256 _incoming,
        uint256 _fromReward
    ) external;

    function withdrawLocking(
        uint256 _seqId,
        address _owner,
        uint256 _nonce,
        uint256 _amount,
        uint256 _locked
    ) external;

    function initializeUnlock(
        uint256 _seqId,
        uint256 _reward,
        uint32 _l2gas,
        ISequencerInfo.Sequencer calldata _seq
    ) external payable;

    function finalizeUnlock(
        address _owner,
        uint256 _seqId,
        uint256 _amount,
        uint256 _reward,
        address _recipient,
        uint32 _l2gas
    ) external payable;

    function liquidateReward(
        uint256 _seqId,
        uint256 _amount,
        address _recipient,
        uint32 _l2gas
    ) external payable;

    function distributeReward(uint256 _batchId, uint256 _totalReward) external;

    function logSignerChange(
        uint256 sequencerId,
        address oldSigner,
        address newSigner,
        uint256 nonce,
        bytes calldata signerPubkey
    ) external;
}

interface/ILockingPool.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity ^0.8.9;

import "./ILockingInfo.sol";
interface ILockingPool is ISequencerInfo {
    struct SequencerData {
        uint256 amount; // sequencer current locked
        uint256 reward; // sequencer current reward that have not cleamed
        uint256 activationBatch; // sequencer activation batch id
        uint256 updatedBatch; // batch id of the last updated
        uint256 deactivationBatch; // sequencer deactivation batch id
        uint256 deactivationTime; // sequencer deactivation timestamp
        uint256 unlockClaimTime; // timestamp that sequencer can claim unlocked token, it's equal to deactivationTime + WITHDRAWAL_DELAY
        uint256 nonce; // sequencer operations number, starts from 1, and used internally by the Metis consencus client
        address owner; // the operator address, owns this sequencer node, it controls lock/relock/unlock/claim functions
        address signer; // sequencer signer, an address to sign L2 blocks, if you want to update it, you must have the privkey of this address
    }

    function lockFor(address _signer, uint256 _amount, bytes calldata _signerPubkey) external;
    function lockWithRewardRecipient(address _signer, address _rewardRecipient, uint256 _amount, bytes calldata _signerPubkey) external;
    function relock(uint256 _seqId, uint256 _amount, bool _lockReward) external;
    function withdrawRewards(uint256 _seqId, uint32 _l2Gas) external payable;
    function unlock( uint256 _seqId, uint32 _l2Gas) external payable;
    function unlockClaim(uint256 _seqId, uint32 _l2Gas) external payable;
    function escrow() external view returns (ILockingInfo);
    function sequencers(uint256 seqId) external view returns (SequencerData memory);
    function seqSigners(address signer) external view returns (uint256 seqId);

    /**
     * @dev Emitted when WITHDRAWAL_DELAY is updated.
     * @param _cur current withdraw delay time
     * @param _prev previours withdraw delay time
     */
    event WithrawDelayTimeChange(uint256 _cur, uint256 _prev);

    /**
     * @dev Emitted when the proxy update threshold in 'updateBlockReward()'.
     * @param newReward new block reward
     * @param oldReward  old block reward
     */
    event RewardUpdate(uint256 newReward, uint256 oldReward);

    /**
     * @dev Emitted when mpc address update in 'UpdateMpc'
     * @param _newMpc new min lock.
     */
    event UpdateMpc(address _newMpc);

    /**
     * @dev Emitted when SignerUpdateThrottle is updated
     * @param _n new min value
     */
    event SetSignerUpdateThrottle(uint256 _n);
    function withdraw(uint256 _seqId, uint256 _amount) external;
}

interface/ISequencerInfo.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;
interface ISequencerInfo {
    error OwnedSequencer();
    error SignerExisted();
    error NoSuchSeq();
    error NullAddress();
    error SeqNotActive();
    error NotSeqOwner();
    error NotSeqSigner();
    error NoRewardRecipient();
    error NotWhitelisted();

    // the sequencer status
    enum Status {
        Unavailabe, // placeholder for default value
        Inactive, // the sequencer will be Inactive if its owner starts unlock
        Active, // the sequencer is active when it locks tokens on the contract
        Unlocked // Exited, the sequencer has no locked tokens, and it's no longer produce blocks on L2
    }

    struct Sequencer {
        uint256 amount; // sequencer current locked
        uint256 reward; // sequencer current reward that have not cleamed
        uint256 activationBatch; // sequencer activation batch id
        uint256 updatedBatch; // batch id of the last updated
        uint256 deactivationBatch; // sequencer deactivation batch id
        uint256 deactivationTime; // sequencer deactivation timestamp
        uint256 unlockClaimTime; // timestamp that sequencer can claim unlocked token, it's equal to deactivationTime + WITHDRAWAL_DELAY
        uint256 nonce; // sequencer operations number, starts from 1, and used internally by the Metis consencus client
        address owner; // the operator address, owns this sequencer node, it controls lock/relock/unlock/claim functions
        address signer; // sequencer signer, an address to sign L2 blocks, if you want to update it, you must have the privkey of this address
        bytes pubkey; // sequencer signer pubkey
        address rewardRecipient; // sequencer rewarder recipient address
        Status status; // sequencer status
    }

    /**
     * @dev Emitted if owner call 'setWhitelist'
     * @param _user the address who can lock token
     * @param _yes white address state
     */
    event SetWhitelist(address _user, bool _yes);

    /**
     * @dev Emitted when reward recipient address update in 'setSequencerRewardRecipient'
     * @param _seqId the sequencerId
     * @param _recipient the address receive reward token
     */
    event SequencerRewardRecipientChanged(uint256 _seqId, address _recipient);

    /**
     * @dev Emitted when sequencer owner is changed
     * @param _seqId the sequencerId
     * @param _owner the sequencer owner
     */
    event SequencerOwnerChanged(uint256 _seqId, address _owner);

    function seqOwners(address owner) external returns (uint256 seqId);
}
